import React from 'react';
import { GoogleMap, useLoadScript, Marker } from '@react-google-maps/api';
import {
    BrowserRouter as Router,
    Routes,
    Route,
} from "react-router-dom";
import Home from "./pages";
import RoadsideBoulders from "pages/RoadsideBoulders";
// import HPBoulders from "./pages/HPBoulders";

import Navbar from './components/Navbar';

const libraries = ['places'];
const mapContainerStyle = {
  width: '100vw',
  height: '100vh',
};
const entrance = {
  lat: 43.9365, // latitude
  lng: -72.9493, // longitude
};
const roadsideBoulders = {
    lat: 43.9368, // latitude
    lng: -72.9496, // longitude
  };
const hpBoulders = {
    lat: 43.9362, // latitude
    lng: -72.9499, // longitude
  };

const App = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'AIzaSyAoSO3XbtcoFi-6X70c6ydxd9tNvOX1H_g',
    libraries,
  });

  if (loadError) {
    return <div>Error loading maps</div>;
  }

  if (!isLoaded) {
    return <div>Loading maps</div>;
  }

  return (
    <Router>
        <div className="App">
            <Navbar />
            <Routes>
            <Route exact path="/" element={<Home />} />
                <Route path="/RoadsideBoulders" element={<RoadsideBoulders />} />
                <Route
                    path="/HPBoulders"
                    element={<HPBoulders />}
                />
            </Routes>
            <div>
                <GoogleMap
                    mapContainerStyle={mapContainerStyle}
                    zoom={18}
                    center={entrance}
                >
                    {/* Midd Gap Entrance */}
                    <Marker
                    position={entrance}
                    icon={"http://maps.google.com/mapfiles/ms/icons/purple-dot.png"}
            />
                    {/* Boulder Area 1 - Roadside Boulders */}
                    <Marker
                    position={roadsideBoulders}
                    icon={"http://maps.google.com/mapfiles/ms/icons/purple-dot.png"}
            />
                    {/* Boulder Area 2 - Harry Potter Boulders */}
                    <Marker
                    position={hpBoulders}
                    icon={"http://maps.google.com/mapfiles/ms/icons/purple-dot.png"}
            />
                </GoogleMap>
            </div>
        </div>
    </Router>
  );
};

export default App;
