import React, { useEffect, useState } from 'react';

const Announcement_Data = ({ baseURL }) => {
  const [username, setUsername] = useState('');
  const [announcements, setAnnouncements] = useState([[]]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch(`${baseURL}/info`, {
          method: 'GET',
        });
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        const data = await res.json();

        if (data.info && data.info.length > 0) {
            const announcementsArray = data.info.map(item => [item.username, item.password]);
            setAnnouncements(announcementsArray);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError(error.toString());
      }};

    fetchData();
  }, [baseURL]); // Dependency array to ensure useEffect runs only once or when baseURL changes

  return (
    <div>
        {announcements.map((announcement, index) => (
        <div key={index}>
            <div>{announcement[0]}</div>
            <br />
            <div>{announcement[1]}</div>
            <br />
            <br />
        </div>
        ))}
  </div>
  );
};

export default Announcement_Data;
