import React, { useEffect, useState } from 'react';

const Announcement_Data = ({ baseURL }) => {
  const [username, setUsername] = useState('');
  const [announcements, setAnnouncements] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch(baseURL + '/info', {
          method: 'GET',
        });
        const data = await res.json();

        if (data.info && data.info.length > 0) {
          setUsername(data.info[0].username);

          let announcementsText = '';
          for (let i = 0; i < data.info.length; i++) {
            announcementsText += data.info[i].username + ' // ';
          }
          setAnnouncements(announcementsText);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [baseURL]); // Dependency array to ensure useEffect runs only once or when baseURL changes

  return (
    <div>
      <input type="text" value={username} readOnly />
      <div>{announcements}</div>
    </div>
  );
};

export default Announcement_Data;
