import React from "react";

const Announcements = () =>
{
    const getBtn = document.getElementById('get');
      const postBtn = document.getElementById('post');
      const input = document.getElementById('input');
      const announcements = document.getElementById('announcements');

      const baseURL = 'http://localhost:8383';

      // **get and populate with data once the webpage is loaded**
      document.addEventListener('DOMContentLoaded', async () => {
        // set to /info since that is the json key for the value set in app.get()
        const res = await fetch(baseURL + '/info', {
          method: 'GET',
        });
        const data = await res.json();
        input.value = data.info[0].username;
        announcements.textContent = '';
        for (let i = 0; i < input.value.length; i++) {
          announcements.textContent += data.info[i].username + ' // ';
        }
      });

      // (the previous fx to get data onClick)
      async function getInfo(e) {
        e.preventDefault(); // needed to prevent the form from refreshing the page
        // set to /info since that is the json key for the value set in app.get()
        const res = await fetch(baseURL + '/info', {
          method: 'GET',
        });
        const data = await res.json();
        input.value = data.info[0].username;
        announcements.textContent = '';
        for (let i = 0; i < input.value.length; i++) {
          announcements.textContent += data.info[i].username + ' // ';
        }
      }

      // **post info**
      async function postInfo(e) {
        e.preventDefault(); // needed to prevent the form from refreshing the page
        if (input.value == '') {
          return;
        }
        const res = await fetch(baseURL, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            parcel: input.value,
          }),
        });
      }

      getBtn.addEventListener('click', getInfo);
      postBtn.addEventListener('click', postInfo);
    return (
        <div class="main-text">
            <h1>Announcements</h1>
            <body>
    <form>
      <input type="text" id="input" />
      <button id="get">GET</button>
      <button id="post">POST</button>
    </form>
    <br /><br /><br />
    <div id="announcements">here are the announcements</div>
  </body>
        </div>
    );
}

export default Announcements;
