import Navbar from './components/Navbar';
import Map from './components/Map';
import {
    BrowserRouter as Router,
    Route,
    Switch,
} from "react-router-dom";
import Home from "./pages/Home";
import RoadsideBoulders from "./pages/RoadsideBoulders";
import HPBoulders from "./pages/HPBoulders";
import MGHome from './pages/MiddGapHome';
import Announcements from './pages/Announcements';

const api = {
    key: '923a598f4c1ab414413e1d115aad7965', // OpenWeather API
    base: 'https://api.openweathermap.org/data/2.5/' // if api not work, this uri may be wrong
};

function App() {

    const [search, setSearch] = useState("");
    const [forecast, setForecast] = useState({});

    const searchPressed = () => {
        fetch(`${api.base}forecast?q=${search}&units=imperial&APPID=${api.key}`)
        // fetch(`api.openweathermap.org/data/2.5/weather?q=${search}&APPID=${api.key}`)
        .then(res => res.json())
        .then((result) => {
            setForecast(result);
            console.log(result);
            console.log(forecast.list[0].main.temp)
        });
    }

    return (
        <div className='App'>
            <header className='App-header'>
                <h1>WEATHER APP</h1>
                <div>
                    <input type="text" placeholder='Search...' onChange={(e) => setSearch(e.target.value)}/>
                    <button onClick={searchPressed}>Search</button>
                </div>

                <p>Location</p>
                <p>{forecast.city.name}</p>

